import React, { useEffect, useRef } from 'react';
import './BlueprintCanvas.css'; // Importa el CSS

const BlueprintCanvas = ({ blueprint }) => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');

    context.clearRect(0, 0, canvas.width, canvas.height);

    // Establecer el color y el ancho de la línea
    context.strokeStyle = 'black';
    context.lineWidth = 2;

    // Comenzar a dibujar
    if (blueprint && blueprint.points) {
      context.beginPath();
      const points = blueprint.points;

      // Mover el lápiz al primer punto
      context.moveTo(points[0].x, points[0].y);

      // Dibujar líneas a cada punto
      points.forEach(point => {
        context.lineTo(point.x, point.y);
      });

      // Terminar el trazo
      context.stroke();
    }
  }, [blueprint]);

  return (
    <div className="canvas-container">
      <canvas
        ref={canvasRef}
        width={800}
        height={600}
      />
    </div>
  );
};

export default BlueprintCanvas;
