import React, { useState } from 'react';
import BlueprintTable from './components/BlueprintTable';
import { getBlueprintsByAuthor } from './services/apiService';

const App = () => {
  const [author, setAuthor] = useState('');
  const [blueprints, setBlueprints] = useState([]);
  const [error, setError] = useState(null);

  // Función para obtener los planos del autor
  const fetchBlueprints = async () => {
    setError(null);  // Reseteamos cualquier error previo
    try {
      const data = await getBlueprintsByAuthor(author);  // Llamamos a la API
      if (data.length === 0) {
        setError('No se encontraron planos para este autor');  // Mensaje si no hay planos
      } else {
        setBlueprints(data);  // Actualizamos los planos obtenidos
      }
    } catch (err) {
      setError('Error al obtener los planos');  // Manejamos errores
      setBlueprints([]);  // Limpiamos los planos si ocurre un error
    }
  };

  // Función para abrir un plano
  const openBlueprint = (blueprint) => {
    alert(`Abriendo el plano: ${blueprint.name}`);  // Acción al abrir un plano
  };

  // Aquí calculamos el total de puntos sumando los puntos de cada plano
  const totalPoints = blueprints.reduce((acc, blueprint) => acc + blueprint.points.length, 0);

  return (
    <div>
      <h1>BluePrints</h1>
      <input
        type="text"
        placeholder="Ingrese el autor"
        value={author}
        onChange={(e) => setAuthor(e.target.value)}  // Actualizamos el estado del autor
      />
      <button onClick={fetchBlueprints}>Get Blueprints</button>

      {/* Mostramos el error si existe */}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {/* Si hay planos, mostramos la tabla y el total de puntos */}
      {blueprints.length > 0 && (
        <>
          <h2>Autor seleccionado: {author}</h2>
          <BlueprintTable blueprints={blueprints} onOpen={openBlueprint} />
          <h3>Total de puntos: {totalPoints}</h3>  {/* Aquí mostramos el total de puntos */}
        </>
      )}
    </div>
  );
};

export default App;
