useEffect(() => {
  console.log('Blueprint recibido:', blueprint); // Añade esta línea
  const canvas = canvasRef.current;
  const context = canvas.getContext('2d');

  context.clearRect(0, 0, canvas.width, canvas.height);

  // Establecer el color y el ancho de la línea
  context.strokeStyle = 'black';
  context.lineWidth = 2;

  if (blueprint && blueprint.points && blueprint.points.length > 0) {
    context.beginPath();
    const points = blueprint.points;

    // Mover el lápiz al primer punto
    context.moveTo(points[0].x, points[0].y);

    // Dibujar líneas a cada punto
    points.forEach(point => {
      context.lineTo(point.x, point.y);
    });

    // Terminar el trazo
    context.stroke();
  } else {
    console.error('No hay puntos para dibujar');
  }
}, [blueprint]);
