import React, { useState, useEffect } from 'react';

const BlueprintsList = () => {
  const [blueprints, setBlueprints] = useState([]);
  const [error, setError] = useState(null);

  const fetchBlueprints = async () => {
    try {
      const response = await fetch('http://localhost:8080/blueprints');
      if (!response.ok) {
        throw new Error('Error al obtener los planos');
      }
      const data = await response.json();
      setBlueprints(data);
    } catch (err) {
      setError(err.message);
    }
  };

  useEffect(() => {
    fetchBlueprints();
  }, []);

  return (
    <div>
      <h1>Blueprints</h1>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <ul>
        {blueprints.map((blueprint, index) => (
          <li key={index}>
            {blueprint.name} by {blueprint.author} - {blueprint.points.length} points
          </li>
        ))}
      </ul>
    </div>
  );
};

export default BlueprintsList;
